
public class Add1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=6;
		int c=a+b;
		System.out.println("Output value of C is "+c);
		
		if(c>10)
		{
			System.out.println("C is greater than 10");
		}
		else
		{
			System.out.println("C is smaller than 10");
		}

	}

}
