
public class Ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=8;
		int b=9;
		if(a>b)
		{
			System.out.println("a is greater than b");
		}
		else if(a<b)
		{
		System.out.println("a is smaller than b");	
		}
		else
		{
			System.out.println("a is equal to b");
		}

	}

}
