package dailyworkouts;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = -34;
		int b = -7;
		 
		a=a+b;
		b=a-b;
		a=a-b;
		
		System.out.println("value of a is "+a);
		System.out.println("value of b is "+b);

	}

}
